import React , {Component} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import '../Styles/Contact.css';


const emailRegex = RegExp(/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/);

const formValid = ({formErrors, ...rest} ) => {
  let valid = true;

  //validate form errors being empty
  Object.values(formErrors).forEach( val => {
    val.length > 0 && (valid = false)
  });

// validate the form was filled out 
  Object.values(rest).forEach(val => {
    val === null  && (valid = false)
  });


return valid;

}


class ContactForm extends Component  {

constructor(props){
  super(props);
  this.state ={
    firstName: null,
    lastName: null,
    email: null,
    message: null,
    // these are error messages to validate the forms
    formErrors: {
      firstName: "",
      lastName: "",
      email: "",
      message: ""
    }
  }
}

handleSubmit = e => {
  e.preventDefault();
  if(formValid(this.state)){
    console.log(`

    ---Submitting--
  First Name:  ${this.state.firstName}
  Last Name:   ${this.state.lastName}
  Email:       ${this.state.email}
  Message:     ${this.state.message}

    `)


  }
  else{
    console.error('Form Invalid - Display error message');
  }
};

handleChange = e => {
  e.preventDefault();
  const { name , value } = e.target;

  let formErrors = this.state.formErrors;



  switch (name){
    case 'firstName':
      formErrors.firstName = value.length > 0 ? '' : 'Please enter your first name';
      break;

    case 'lastName':
      formErrors.lastName = value.length > 0  ? '' : 'Please enter your last name';
      break;

    case 'email':
        formErrors.email = emailRegex.test(value) && value.length > 0 ? '' : 'Invalid Email Address';
        break;

    case 'message':
          formErrors.message = value.length > 0  ? '' : 'Please enter a message';
          break;

    default:
      break;


  }
  this.setState({ formErrors, [name]:value}, ()=> console.log(this.state));
}






render() {

  const { formErrors } = this.state;
return(
<Col className="colContact" lg={6}>
<link href="https://fonts.googleapis.com/css?family=Fredericka+the+Great|Lateef|Open+Sans|Rancho|Permanent+Marker&display=swap" rel="stylesheet"></link>
<h1>Let's Talk</h1>
<Form onSubmit={this.handleSubmit}noValidate>
<Form.Group controlId="formBasic">
 <Form.Label >First Name</Form.Label>
 <Form.Control required type="text" placeholder="First Name" name="firstName"  noValidate onChange={this.handleChange}/> 
</Form.Group>

{formErrors.firstName.length > 0 && (
  <span className="errorMessage">{formErrors.firstName}</span>
)}

<Form.Group controlId="formBasic">
 <Form.Label>Last Name</Form.Label>
 <Form.Control required type="text" placeholder="Last Name" name="lastName" noValidate onChange={this.handleChange} />
</Form.Group>

{formErrors.lastName.length > 0 && (
  <span className="errorMessage">{formErrors.lastName}</span>
)}

<Form.Group controlId="formBasicEmail">
   <Form.Label>E-Mail Address</Form.Label>
    <Form.Control required type="email" placeholder="E-Mail Address" name="email" noValidate onChange={this.handleChange} />
</Form.Group>

{formErrors.email.length > 0 && (
  <span className="errorMessage">{formErrors.email}</span>
)}


<Form.Group controlId="formBasicTextArea">
  <Form.Label>Message</Form.Label>
  <Form.Control  required as="textarea" rows="3" name="message" noValidate onChange={this.handleChange} />
</Form.Group>
{formErrors.message.length > 0 && (
  <span className="errorMessage">{formErrors.message}</span>
)}
  <br/>
  <br/>
<Button variant="light" type="submit">Submit </Button>
</Form>
</Col>
  )





}



}



export default ContactForm;
