import React , {Component} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import '../Styles/Contact.css';


const emailRegex = RegExp(/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/);

const formValid = formErrors =>{
  let valid = true;
  Object.values(formErrors).forEach( val => {
    val.length > 0 && (valid = false)
  });

  return valid;
}


class ContactForm extends Component  {

constructor(props){
  super(props);
  this.state ={
    firstName: null,
    lastName: null,
    email: null,
    message: null,
    // these are error messages to validate the forms
    formErrors: {
      firstName: "",
      lastName: "",
      email: "",
      message: ""
    }
  }
}

handleSubmit = e => {
  e.preventDefault();
  if(formValid(this.state.formErrors)){
    console.log(`

    ---Submitting--
  First Name:  ${this.state.firstName}
  Last Name:  ${this.state.lastName}
  Email:  ${this.state.email}
  Message: ${this.state.message}

    `)
  }
  else{
    console.error('Form Invalid - Display error message');
  }
};

handleChange = e => {
  e.preventDefault();
  const { name , value } = e.target;

  let formErrors = this.state.formErrors;



  switch (name){
    case 'firstName':
      formErrors.firstName = value.length = "" ? 'Please enter your first name' : '';
      break;

    case 'lastName':
      formErrors.lastName = value.length > 0  ? 'Please enter your last name' : '';
      break;

    case 'email':
        formErrors.email = emailRegex.test(value) && value.length > 0 ? '' : 'Invalid Email Address';
        break;

    default:
      break;


  }
  this.setState({ formErrors, [name]:value}, ()=> console.log(this.state));
}






render() {

  const { formErrors } = this.state;
return(
<Col className="colContact" lg={6}>
<Form onSubmit="this.handleSubmit" noValidate>
<Form.Group controlId="formBasic">
 <Form.Label >First Name</Form.Label>
 <Form.Control type="text" placeholder="First Name" name="firstName" required noValidate onChange={this.handleChange}/> 
</Form.Group>

{formErrors.firstName.length > 0 && (
  <span className="errorMessage">{formErrors.firstName}</span>
)}

<Form.Group controlId="formBasic">
 <Form.Label>Last Name</Form.Label>
 <Form.Control type="text" placeholder="Last Name" name="lastName" noValidate onChange={this.handleChange} />
</Form.Group>

<Form.Group controlId="formBasicEmail">
   <Form.Label>E-Mail Address</Form.Label>
    <Form.Control type="email" placeholder="E-Mail Address" name="email" noValidate onChange={this.handleChange} />
</Form.Group>

<Form.Group controlId="formBasicTextArea">
  <Form.Label>Message</Form.Label>
  <Form.Control as="textarea" rows="3" name="message" noValidate onChange={this.handleChange} />
</Form.Group>

<Button variant="light" type="submit">Submit </Button>
</Form>
</Col>
  )





}



}



export default ContactForm;
