import React , {Component} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import '../Styles/Contact.css';


class ContactForm extends Component  {

constructor(props){
  super(props);
  this.state ={
    firstName: null,
    lastName: null,
    email: null,
    message: null,
    // these are error messages to validate the forms
    formErrors: {
      firstName: "",
      lastName: "",
      email: "",
      message: ""
    }
  }
}

handleSubmit = e => {
  e.preventDefault();
  if(formValid(this.state.formErrors)){
    console.log(`
    ---first name--
    ${this.state.firstName}
    ${this.state.lastName}
    ${this.state.email}
    ${this.state.message}
    `)
  }

}






render() {
return(
<Col className="colContact" lg={6}>
<Form onSubmit="this.handleSubmit" noValidate>
<Form.Group controlId="formBasic">
 <Form.Label >First Name</Form.Label>
 <Form.Control type="text" placeholder="First Name" required noValidate onChange={this.handleChange}/> 
</Form.Group>

<Form.Group controlId="formBasic">
 <Form.Label>Last Name</Form.Label>
 <Form.Control type="text" placeholder="Last Name" noValidate onChange={this.handleChange} />
</Form.Group>

<Form.Group controlId="formBasicEmail">
   <Form.Label>E-Mail Address</Form.Label>
    <Form.Control type="email" placeholder="E-Mail Address"  noValidate onChange={this.handleChange} />
</Form.Group>

<Form.Group controlId="formBasicTextArea">
  <Form.Label>Message</Form.Label>
  <Form.Control as="textarea" rows="3"  noValidate onChange={this.handleChange} />
</Form.Group>

<Button variant="light" type="submit">Submit </Button>
</Form>
</Col>
  )





}



}



export default ContactForm;
